mapper.select:
select count(*) from `order_product` inner join `order` on `order_product`.order_idx = `order`.order_idx inner join product on `order_product`.product_idx = product.product_idx left outer join review on `order_product`.order_product_idx = review.order_product_idx
`order`.client_num=#{member_idx} and review.member_idx is null
order by order_date desc;

select * from `order_product` inner join `order` on `order_product`.order_idx = `order`.order_idx inner join product on `order_product`.product_idx = product.product_idx inner join review on `order_product`.order_product_idx = review.order_product_idx
`order`.client_num=#{member_idx} and review.member_idx=#{member_idx}
order by order_date desc

select count(*) from `order_product` inner join `order` on `order_product`.order_idx = `order`.order_idx inner join product on `order_product`.product_idx = product.product_idx inner join review on `order_product`.order_product_idx = review.order_product_idx
`order`.client_num=#{member_idx} and review.member_idx=#{member_idx}
order by order_date desc;

select review_idx from review 
     
    member_idx= #{member_idx}
    and product_idx = #{product_idx}
    and order_product_idx= #{order_product_idx}

select * from file
    inner join review
    on file.review_idx = review.review_idx
            where review.member_idx=#{member_idx}
            and review.review_idx=#{review_idx}
            order by file_idx

select * from file
    inner join review
    on file.review_idx = review.review_idx
            where review.member_idx=#{member_idx}
            and review.review_idx=#{review_idx}
            order by file_idx

mapper.update:
