mapper.select:
select * from delivery
    where member_idx=#{member_idx}
    order by defaultpost desc;

select * from product_inq inner join product on product.product_idx = product_inq.product_idx inner join file on product_inq.product_idx = file.product_idx
product_inq.member_idx=#{member_idx} and file.product_idx IS NOT NULL AND (file.product_idx, file.file_idx) IN ( SELECT file.product_idx, MIN(file.file_idx) FROM file GROUP BY file.product_idx )
order by product_inq_regDate desc;

select * from product_inq
    inner join product
    on product.product_idx = product_inq.product_idx
    where product_inq.member_idx=#{member_idx}  
        order by product_inq_regDate desc;

select count(*) from product_inq
    inner join product
    on product.product_idx = product_inq.product_idx
    where product_inq.member_idx=#{member_idx}  
        order by product_inq_regDate desc;

mapper.delete:
        delete from product_inq
			where product_inq_idx=#{product_inq_idx}
		    and member_idx=#{member_idx}

mapper.update:
        update product_inq set product_inq_content=#{product_inq_content}
					where product_inq_idx=#{product_inq_idx}
                    and member_idx=#{member_idx}

        update `member` set member_handphone=#{member_handphone}, member_email=#{member_email},
        member_nickname=#{member_nickname},member_postNum=#{member_postNum},
        member_address=#{member_address},member_address2=#{member_address2}
		where member_idx=#{member_idx}

mapper.select:
select * from product_inq inner join product on product.product_idx = product_inq.product_idx inner join file on product_inq.product_idx = file.product_idx
product.member_idx=#{member_idx} and file.product_idx IS NOT NULL AND (file.product_idx, file.file_idx) IN ( SELECT file.product_idx, MIN(file.file_idx) FROM file GROUP BY file.product_idx )
order by product_inq_regDate desc;

select * from product_inq
    inner join product
    on product.product_idx = product_inq.product_idx
    where product.member_idx=#{member_idx}
        order by product_inq_regDate desc;

select count(*) from product_inq
    inner join product
    on product.product_idx = product_inq.product_idx
    where product.member_idx=#{member_idx}  
        order by product_inq_regDate desc;
