mapper.select:
select * from `order_product` inner join `order` on `order_product`.client_num = `order`.client_num inner join product on `order_product`.product_idx = product.product_idx inner join review on `order_product`.product_idx = review.product_idx
`order`.client_num=#{member_idx} and review.member_idx!=#{member_idx}
order by order_date desc

select count(*) from `order_product` inner join `order` on `order_product`.client_num = `order`.client_num inner join product on `order_product`.product_idx = product.product_idx inner join review on `order_product`.product_idx = review.product_idx
`order`.client_num=#{member_idx} and review.member_idx!=#{member_idx}
order by order_date desc;

select * from `order_product` inner join `order` on `order_product`.client_num = `order`.client_num inner join product on `order_product`.product_idx = product.product_idx inner join review on `order_product`.product_idx = review.product_idx inner join file on review.product_idx = file.product_idx
`order`.client_num=#{member_idx} and review.member_idx=#{member_idx} and file.product_idx IS NOT NULL AND (file.product_idx, file.file_idx) IN ( SELECT file.product_idx, MIN(file.file_idx) FROM file GROUP BY file.product_idx )
order by order_date desc;

