mapper.select:
	select * from `order` where client_num=#{member_idx} 
    and order_date >= #{timestamp_begin}
	order by `order_date` desc;

	select * from `order` where client_num=#{member_idx} 
    and order_date >= #{timestamp_begin}
    and order_ing = 4
	order by `order_date` desc;

	select count(*) from `order` where client_num=#{member_idx}
    and order_date >= #{timestamp_begin}
    and order_ing = 4
