mapper.update:
update basket 
	set basket_count = basket_count+1 
	where product_idx = #{product_idx}
	and client_num=#{client_num}

update basket 
	set basket_count = basket_count-1 
	where product_idx = #{product_idx}
	and client_num=#{client_num}

update basket 
	set basket_count = #{basket_count} 
	where product_idx = #{product_idx}
	and client_num=#{client_num}

update basket 
	set basket_count = basket_count+#{basket_count}
	where product_idx = #{product_idx}
	and client_num=#{client_num}
