mapper.select:
        select count(*) from product
            
	            
	                    and product_name like CONCAT('%',#{searchWord},'%')                                                                           
	            
	            
                        and category_code = #{category_code}
                
                
                        and category_code_small = #{category_code_small}
                
                
                        
                            and product_price>= #{product_price_min}
                            and product_price<= #{product_price_max}
                        
                
                
                        
                        and product_price>= #{product_price_min}                         
                        
                
                
                                                 
                        and product_price<= #{product_price_max}
                        
                                
                and product_del_or_not=1
                                         
            order by product_idx desc

select.if:
select * from ( (select * from product order by product_idx) a inner join (select review.product_idx, avg(review_score) from review inner join product on product.product_idx = review.product_idx group by product.product_idx order by avg(review_score) desc) b on a.product_idx = b.product_idx)
and a.product_name like CONCAT('%',#{searchWord},'%')
and a.category_code = #{category_code}
and a.category_code_small = #{category_code_small}
and product_price>= #{product_price_min} and product_price<= #{product_price_max}
and product_price>= #{product_price_min}
and product_price<= #{product_price_max}
and product_del_or_not=1

select * from product            
                              
        
                and product_name like CONCAT('%',#{searchWord},'%')                                                                           
                        
                                                        
        
                and category_code = #{category_code}
        
        
        
                and category_code_small = #{category_code_small}
        
                        
        
                
                    and product_price>= #{product_price_min} 
                    and product_price<= #{product_price_max}
                
        
        
                
                    and product_price>= #{product_price_min}                         
                
        
        
                                         
                    and product_price<= #{product_price_max}
                
        
    and product_del_or_not=1
                        

order by product_idx asc
                       

order by product_regDate desc                     


order by product_capa desc                     


order by product_price desc                     


order by product_price asc

select * from ( (select * from product order by product_idx) a inner join (select review.product_idx, avg(review_score) from review inner join product on product.product_idx = review.product_idx group by product.product_idx order by avg(review_score) desc) b on a.product_idx = b.product_idx) inner join file on file.product_idx = a.product_idx
and a.product_name like CONCAT('%',#{searchWord},'%')
and a.category_code = #{category_code}
and a.category_code_small = #{category_code_small}
and product_price>= #{product_price_min} and product_price<= #{product_price_max}
and product_price>= #{product_price_min}
and product_price<= #{product_price_max}
and file.product_idx IS NOT NULL AND (file.product_idx, file.file_idx) IN ( SELECT file.product_idx, MIN(file.file_idx) FROM file GROUP BY file.product_idx ) and a.product_del_or_not=1

select * from product inner join file on file.product_idx = product.product_idx
and product.product_name like CONCAT('%',#{searchWord},'%')
and product.category_code = #{category_code}
and product.category_code_small = #{category_code_small}
and product_price>= #{product_price_min} and product_price<= #{product_price_max}
and product_price>= #{product_price_min}
and product_price<= #{product_price_max}
and file.product_idx IS NOT NULL AND (file.product_idx, file.file_idx) IN ( SELECT file.product_idx, MIN(file.file_idx) FROM file GROUP BY file.product_idx ) and product.product_del_or_not=1
order by product.product_idx asc
order by product.product_saleCount desc
order by product.product_regDate desc
order by product.product_capa desc
order by product.product_price desc
order by product.product_price asc
