mapper.insert:
		insert into question_board  
		(board_idx, all_board_number, title, content, member_idx, member_nickname 
   		
     		, originfile
  		 
   		
  		   , savefile
   		
		)
		values(question_board _seq.nextval, all_board_seq.nextval, #{title}, #{content}, #{member_idx}, #{member_nickname} 
    	
   	   		, #{originFile}
    	
    	
      		, #{saveFile}
    	
		)

mapper.update:
update question_board  
	set read_count = read_count+1 
	where board_idx = #{board_idx}

update question_board 
	set RECOMMENDATION_COUNT = RECOMMENDATION_COUNT+1
	where board_idx = #{board_idx}

update question_board 
	set NOT_RECOMMENDATION_COUNT = NOT_RECOMMENDATION_COUNT+1
	where board_idx = #{board_idx}
